import java.math.*;class A381853{static BigInteger a(int n){int[]r=new int[n];BigInteger[]k=new BigInteger[n];for(int i=0;i<n;i++)r[i]=i;for(int i=0;i<n;i++){int t=2*i%n,u=r[t];r[t]=r[i];r[i]=u;}for(int i=0;i<n;i++){int x=r[i];BigInteger c=BigInteger.ONE;while(x!=i){c=c.add(BigInteger.ONE);x=r[x];}k[i]=c;}BigInteger m=k[0];for(int i=1;i<n;i++)m=m.multiply(k[i]).divide(m.gcd(k[i]));return m;}}
